import React from 'react';
import Navegacion from './Navegacion';
import personas from './personas';

const Estadisticas = () => {
  // Convertir edades a números para comparaciones
  const personasConEdad = personas.map(p => ({ ...p, edad: parseInt(p.edad) }));

  const mayoresDe35 = personasConEdad.filter(p => p.edad > 35);
  const personaMayorEdad = personasConEdad.reduce((max, p) => p.edad > max.edad ? p : max, personasConEdad[0]);
  const personaMenorEdad = personasConEdad.reduce((min, p) => p.edad < min.edad ? p : min, personasConEdad[0]);

  const personasMayorEdad = personasConEdad.filter(p => p.edad === personaMayorEdad.edad).map(p => p.nombre);
  const personasMenorEdad = personasConEdad.filter(p => p.edad === personaMenorEdad.edad).map(p => p.nombre);

  return (
    <div>
      <Navegacion />
      <h1>Estadísticas</h1>
      <p>Cuántos mayores de 35 años hay: {mayoresDe35.length}</p>
      <p>Persona(s) de mayor edad: {personasMayorEdad.join(', ')}</p>
      <p>Persona(s) de menor edad: {personasMenorEdad.join(', ')}</p>
    </div>
  );
};

export default Estadisticas;
