import React from 'react';

const personas = [
  { id: 1, nombre: 'Juan', edad: 30 },
  { id: 2, nombre: 'Ana', edad: 40 },
  { id: 3, nombre: 'Luis', edad: 25 }
];

const Estadisticas = () => {
  const mayoresDe35 = personas.filter(p => p.edad > 35);
  const personaMayorEdad = personas.reduce((max, p) => p.edad > max.edad ? p : max, personas[0]);
  const personaMenorEdad = personas.reduce((min, p) => p.edad < min.edad ? p : min, personas[0]);

  const personasMayorEdad = personas.filter(p => p.edad === personaMayorEdad.edad).map(p => p.nombre);
  const personasMenorEdad = personas.filter(p => p.edad === personaMenorEdad.edad).map(p => p.nombre);

  return (
    <div>
      <h1>Estadísticas</h1>
      <p>Cuántos mayores de 35 años hay: {mayoresDe35.length}</p>
      <p>Persona(s) de mayor edad: {personasMayorEdad.join(', ')}</p>
      <p>Persona(s) de menor edad: {personasMenorEdad.join(', ')}</p>
      <nav>
        <Link to="/">Home</Link> | <Link to="/estadisticas">Estadísticas</Link> | <Link to="/contacto">Contacto</Link>
      </nav>
    </div>
  );
};

export default Estadisticas;
