import React from 'react';
import personas from './Personas';

const Estadisticas = () => {
  // Convertir edades a números para comparaciones
  const personasConEdad = personas.map(p => ({ ...p, edad: parseInt(p.edad) }));

  // Encontrar la edad máxima y mínima
  const edades = personasConEdad.map(p => p.edad);
  const edadMaxima = Math.max(...edades);
  const edadMinima = Math.min(...edades);

  // Filtrar las personas con la edad máxima y mínima
  const personasMayorEdad = personasConEdad.filter(p => p.edad === edadMaxima).map(p => p.nombre);
  const personasMenorEdad = personasConEdad.filter(p => p.edad === edadMinima).map(p => p.nombre);

  return (
    <div>
      <h1>Estadísticas</h1>
      <p>Cuántos mayores de 35 años hay: {personasConEdad.filter(p => p.edad > 35).length}</p>
      <p>Persona(s) de mayor edad: {personasMayorEdad.join(', ')}</p>
      <p>Persona(s) de menor edad: {personasMenorEdad.join(', ')}</p>
    </div>
  );
};

export default Estadisticas;
